export async function getLatestNpmVersion(pkg) {
    const url = `https://registry.npmjs.org/${pkg}/latest`;
    try {
        const res = await fetch(url);
        if (!res.ok)
            throw new Error(`Failed to fetch version for ${pkg}`);
        const data = await res.json();
        return data.version;
    }
    catch {
        return 'latest';
    }
}
